    public void deviceIdCorrelationStart() {
        SharedPreferences defaultSharedPreferences = PreferenceManager.getDefaultSharedPreferences(getApplicationContext());
        int i = defaultSharedPreferences.getInt("correlationAttempts", 10);
        if (i == 0) {
            return;
        }
        Intent intent = new Intent();
        intent.setComponent(new ComponentName(getPackageName().contains("trial") ? BuildConfig.APPLICATION_ID : "com.mojang.minecrafttrialpe", "com.mojang.minecraftpe.ImportService"));
        bindService(intent, this.mConnection, Context.BIND_AUTO_CREATE);
        SharedPreferences.Editor edit = defaultSharedPreferences.edit();
        edit.putInt("correlationAttempts", i - 1);
        edit.apply();
    }

