    @SuppressLint("HandlerLeak")
    class IncomingHandler extends Handler {
        IncomingHandler() {
        }

        @Override
        public void handleMessage(@NonNull Message msg) {
            if (msg.what == MainActivity.MSG_CORRELATION_RESPONSE) {
                String packageName = getApplicationContext().getPackageName();
                SharedPreferences defaultSharedPreferences = PreferenceManager.getDefaultSharedPreferences(getApplicationContext());
                defaultSharedPreferences.getString("deviceId", "");
                try {
                    long j = getPackageManager().getPackageInfo(packageName, 0).firstInstallTime;
                    String string = msg.getData().getString("deviceId");
                    String string2 = msg.getData().getString("sessionId");
                    long j2 = msg.getData().getLong("time");
                    if (j > j2) {
                        defaultSharedPreferences.edit().apply();
                        nativeDeviceCorrelation(j, string, j2, string2);
                    }
                    SharedPreferences.Editor edit = defaultSharedPreferences.edit();
                    edit.putInt("correlationAttempts", 0);
                    edit.apply();
                    if (mBound != MessageConnectionStatus.CONNECTED) {
                        return;
                    }
                    MainActivity mainActivity = MainActivity.this;
                    mainActivity.unbindService(mainActivity.mConnection);
                    return;
                } catch (PackageManager.NameNotFoundException unused) {
                    return;
                }
            }
            super.handleMessage(msg);
        }
    }

