    public void startTextToSpeech(String s) {
        if (this.textToSpeechManager != null) {
            if (Build.VERSION.SDK_INT < 21) {
                HashMap<String, String> hashMap = new HashMap<>();
                hashMap.put("volume", String.valueOf(this.mVolume));
                this.textToSpeechManager.speak(s, 0, hashMap);
                return;
            }
            Bundle bundle = new Bundle();
            bundle.putFloat("volume", this.mVolume);
            this.textToSpeechManager.speak(s, 0, bundle, null);
        }
    }

